import React, { useState } from "react";
import '../../style/navbar.css';
import { NavLink, useNavigate } from "react-router-dom";
import ApiService from "../../service/ApiService";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearch, faShoppingCart, faTimes, faBars } from '@fortawesome/free-solid-svg-icons';

const Navbar = () => {
    const [searchValue, setSearchValue] = useState("");
    const [isSearchFocused, setIsSearchFocused] = useState(false);
    const [isMenuOpen, setIsMenuOpen] = useState(false);
    const navigate = useNavigate();

    const isAdmin = ApiService.isAdmin();
    const isAuthenticated = ApiService.isAuthenticated();

    const handleSearchChange = (e) => {
        setSearchValue(e.target.value);
    }

    const handleSearchSubmit = async (e) => {
        e.preventDefault();
        navigate(`/?search=${searchValue}`);
    }

    const handleSearchFocus = () => {
        setIsSearchFocused(true);
    }

    const handleSearchBlur = () => {
        setIsSearchFocused(false);
    }

    const handleClearSearch = () => {
        setSearchValue("");
        handleSearchFocus(); // Correct function call
    }

    const handleLogout = () => {
        const confirm = window.confirm("Are you sure you want to logout?");
        if (confirm) {
            ApiService.logout();
            setTimeout(() => {
                navigate('/login');
            }, 500);
        }
    }

    const toggleMenu = () => {
        setIsMenuOpen(!isMenuOpen);
    }

    return (
        <nav className="navbar">
            <div className="navbar-brand">
                <NavLink to="/"> <img src="./FashionFlareLogo.png" alt="Fashion Flare" /></NavLink>
            </div>
            <button className="menu-toggle" onClick={toggleMenu}>
                <FontAwesomeIcon icon={faBars} />
            </button>
            <form className={`navbar-search ${isSearchFocused ? 'focused' : ''}`} onSubmit={handleSearchSubmit}>
                <input 
                    type="text" 
                    placeholder="Search products" 
                    value={searchValue}
                    onChange={handleSearchChange} 
                    onFocus={handleSearchFocus}
                    onBlur={handleSearchBlur}
                />
                {searchValue && (
                    <button type="button" className="clear-btn" onClick={handleClearSearch}>
                        <FontAwesomeIcon icon={faTimes} />
                    </button>
                )}
                <button type="submit" className="search-btn">
                    <FontAwesomeIcon icon={faSearch} />
                </button>
            </form>
            <div className={`navbar-links ${isMenuOpen ? 'open' : ''}`}>
                <NavLink to="/" activeClassName="active">Home</NavLink>
                <NavLink to="/categories" activeClassName="active">Categories</NavLink>
                <NavLink to="/team" activeClassName="active">Team</NavLink>
                {isAuthenticated && <NavLink to="/profile" activeClassName="active">My Account</NavLink>}
                {isAdmin && <NavLink to="/admin" activeClassName="active">Admin</NavLink>}
                {!isAuthenticated && <NavLink to="/login" activeClassName="active">Login</NavLink>}
                {isAuthenticated && <NavLink onClick={handleLogout} className="logout-btn">Logout</NavLink>}
                <NavLink to="/cart" className="cart-link">
                    <FontAwesomeIcon icon={faShoppingCart} className="cart-icon" />
                </NavLink>
            </div>
        </nav>
    );
}

export default Navbar;
