import React, { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import ProductList from "../common/ProductList";
import Pagination from "../common/Pagination";
import ApiService from "../../service/ApiService";
import Carousel from 'react-bootstrap/Carousel';
import 'bootstrap/dist/css/bootstrap.min.css';
import '../../style/home.css';

const Home = () => {
    const location = useLocation();
    const [products, setProducts] = useState([]);
    const [currentPage, setCurrentPage] = useState(1);
    const [totalPages, setTotalPages] = useState(0);
    const [error, setError] = useState(null);
    const itemsPerPage = 10;

    useEffect(() => {
        const fetchProducts = async () => {
            try {
                let allProducts = [];
                const queryparams = new URLSearchParams(location.search);
                const searchItem = queryparams.get('search');

                if (searchItem) {
                    const response = await ApiService.searchProducts(searchItem);
                    allProducts = response.productList || [];
                } else {
                    const response = await ApiService.getAllProducts();
                    allProducts = response.productList || [];
                }

                setTotalPages(Math.ceil(allProducts.length / itemsPerPage));
                setProducts(allProducts.slice((currentPage - 1) * itemsPerPage, currentPage * itemsPerPage));
            } catch (error) {
                setError(error.response?.data?.message || error.message || 'Unable to fetch products');
            }
        }

        fetchProducts();
    }, [location.search, currentPage]);

    return (
        <div className="home">
            <Carousel
                className="mb-4 custom-carousel"
                interval={5000} // Autoplay interval
                controls={true} // Show controls
                indicators={true} // Show indicators
                pause="hover" // Pause on hover
                wrap={true} // Allow wrapping
            >
                <Carousel.Item>
                    <img
                        className="d-block w-100 carousel-img"
                        src="./saree1.png"
                        alt="First slide"
                    />
                    <Carousel.Caption className="custom-caption">
                        <h3>Embrace Elegance, Drape Tradition</h3>
                        <p>Discover the timeless beauty of sarees with our exquisite collection.</p>
                        <a href="/shop" className="btn btn-primary">Shop Now</a>
                    </Carousel.Caption>
                </Carousel.Item>
                <Carousel.Item>
                    <img
                        className="d-block w-100 carousel-img"
                        src="./saree2.png"
                        alt="Second slide"
                    />
                    <Carousel.Caption className="custom-caption">
                        <h3>Get 10% Discount</h3>
                        <p>On minimum purchase of 2500.</p>
                        <a href="/offers" className="btn btn-primary">Explore Offers</a>
                    </Carousel.Caption>
                </Carousel.Item>
                <Carousel.Item>
                    <img
                        className="d-block w-100 carousel-img"
                        src="https://www.mysoresareeudyog.com/media/mageplaza/blog/post/w/h/whatsapp_image_2024-01-16_at_2.19.45_pm.jpeg"
                        alt="Third slide"
                    />
                    <Carousel.Caption className="custom-caption">
                        <h3>New Arrivals</h3>
                        <p>Check out our latest collection of sarees.</p>
                        <a href="/new-arrivals" className="btn btn-primary">View New Arrivals</a>
                    </Carousel.Caption>
                </Carousel.Item>
            </Carousel>

            {error ? (
                <p className="error-message">{error}</p>
            ) : (
                <div className="my-background">
                    <ProductList products={products} />
                    <Pagination
                        currentPage={currentPage}
                        totalPages={totalPages}
                        onPageChange={(page) => setCurrentPage(page)}
                    />
                </div>
            )}
        </div>
    );
};

export default Home;
