import React from 'react';
import '../../style/Team.css'; 

const Team = () => {
  const teamMembers = [
    { name: 'Arusa Korse', photo: 'arusa.jpg', role: 'Developer' },
    { name: 'Tejas Kolhe', photo: './Tejas Kolhe.jpg', role: 'Designer' },
    { name: 'Shreyash Bhagat', photo: 'shreyash.jpg', role: 'Project Manager' },
    { name: 'Shraddha Rathi', photo: './shraddha.jpg', role: 'QA Engineer' },
    { name: 'Swapnali Patil', photo: './swapnali.jpg', role: 'Developer' }
  ];

  return (
    <div className="team-container">
      <h1>Meet Our Team</h1>
      <div className="team-grid">
        {teamMembers.map((member, index) => (
          <div className="team-member" key={index}>
            <img src={member.photo} alt={member.name} className="team-photo" />
            <h2 className="team-name">{member.name}</h2>
            <p className="team-role">{member.role}</p>
            <div className="tooltip">
              <span className="view-more-btn">More Info</span>
              <span className="tooltiptext">Connect with {member.name}</span>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Team;
